import Foundation

//https://www.hackerrank.com/challenges/ctci-making-anagrams/

func makeAnagram(a: String, b: String) -> Int {
    
    if a.isEmpty || b.isEmpty {
        return 0
    }
    
    let ab_total = a.count + b.count
    var a_dict: [String: Int] = [:]
    var b_dict: [String: Int] = [:]
    var anagram = ""
    
    _ = a.map { a_dict[String($0), default: 0] += 1 }
    _ = b.map { b_dict[String($0), default: 0] += 1}
    
    
    for (k,v) in a_dict {
    
        if b_dict[k] != nil{
            
            let count = (b_dict[k]! + v ) / 2
                
            for _ in 0..<count {
                anagram += k
            }
            
            print("found \( b_dict[k]!) + \(v)  = \(count)[\(k)]")
        }
    
    
    }
    
//    print(a_dict)
//    print(b_dict)
//    print(ab_total,anagram)
    print(anagram.count)
    return ab_total  - anagram.count * 2
}

